import {
  MERCHANT_CREATE_REVIEW_FAIL,
  MERCHANT_CREATE_REVIEW_REQUEST,
  MERCHANT_CREATE_REVIEW_RESET,
  MERCHANT_CREATE_REVIEW_SUCCESS,
  MERCHANT_DETAILS_FAIL,
  MERCHANT_DETAILS_REQUEST,
  MERCHANT_DETAILS_SUCCESS,
  MERCHANT_LIST_FAIL,
  MERCHANT_LIST_REQUEST,
  MERCHANT_LIST_SUCCESS,
} from "../Constants/MerchantConstants";

// MERCHANT LIST
export const merchantListReducer = (state = { merchants: [] }, action) => {
  switch (action.type) {
    case MERCHANT_LIST_REQUEST:
      return { loading: true, merchants: [] };
    case MERCHANT_LIST_SUCCESS:
      return {
        loading: false,
        pages: action.payload.pages,
        page: action.payload.page,
        merchants: action.payload.merchants,
      };
    case MERCHANT_LIST_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

// SINGLE MERCHANT
export const merchantDetailsReducer = (
  state = { merchant: { reviews: [] } },
  action
) => {
  switch (action.type) {
    case MERCHANT_DETAILS_REQUEST:
      return { ...state, loading: true };
    case MERCHANT_DETAILS_SUCCESS:
      return { loading: false, merchant: action.payload };
    case MERCHANT_DETAILS_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

// MERCHANT REVIEW CREATE
export const merchantCreateReviewReducer = (state = {}, action) => {
  switch (action.type) {
    case MERCHANT_CREATE_REVIEW_REQUEST:
      return { loading: true };
    case MERCHANT_CREATE_REVIEW_SUCCESS:
      return { loading: false, success: true };
    case MERCHANT_CREATE_REVIEW_FAIL:
      return { loading: false, error: action.payload };
    case MERCHANT_CREATE_REVIEW_RESET:
      return {};
    default:
      return state;
  }
};
