import axios from "axios";
import {
  MERCHANT_CREATE_REVIEW_FAIL,
  MERCHANT_CREATE_REVIEW_REQUEST,
  MERCHANT_CREATE_REVIEW_SUCCESS,
  MERCHANT_DETAILS_FAIL,
  MERCHANT_DETAILS_REQUEST,
  MERCHANT_DETAILS_SUCCESS,
  MERCHANT_LIST_FAIL,
  MERCHANT_LIST_REQUEST,
  MERCHANT_LIST_SUCCESS,
} from "../Constants/MerchantConstants";
import { logout } from "./userActions";

// MERCHANT LIST
export const listMerchant =
  (keyword = " ", pageNumber = " ") =>
  async (dispatch) => {
    try {
      dispatch({ type: MERCHANT_LIST_REQUEST });
      const { data } = await axios.get(
        `/api/merchants?keyword=${keyword}&pageNumber=${pageNumber}`
      );
      dispatch({ type: MERCHANT_LIST_SUCCESS, payload: data });
    } catch (error) {
      dispatch({
        type: MERCHANT_LIST_FAIL,
        payload:
          error.response && error.response.data.message
            ? error.response.data.message
            : error.message,
      });
    }
  };

// SINGLE MERCHANT
export const listMerchantDetails = (id) => async (dispatch) => {
  try {
    dispatch({ type: MERCHANT_DETAILS_REQUEST });
    const { data } = await axios.get(`/api/merchants/${id}`);
    dispatch({ type: MERCHANT_DETAILS_SUCCESS, payload: data });
  } catch (error) {
    dispatch({
      type: MERCHANT_DETAILS_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

// MERCHANT REVIEW CREATE
export const createMerchantReview =
  (merchantId, review) => async (dispatch, getState) => {
    try {
      dispatch({ type: MERCHANT_CREATE_REVIEW_REQUEST });

      const {
        userLogin: { userInfo },
      } = getState();

      const config = {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${userInfo.token}`,
        },
      };

      await axios.post(`/api/merchants/${merchantId}/review`, review, config);
      dispatch({ type: MERCHANT_CREATE_REVIEW_SUCCESS });
    } catch (error) {
      const message =
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message;
      if (message === "Not authorized, token failed") {
        dispatch(logout());
      }
      dispatch({
        type: MERCHANT_CREATE_REVIEW_FAIL,
        payload: message,
      });
    }
  };
